import React, {useState} from 'react';
import { fetchQuizQuestions } from './Components/API';

// Components
import QuestionCard from './Components/QuestionCard';
// Types
import { QuestionState, Difficulty } from './Components/API';
// Styles
import { GlobalStyle, Wrapper } from './App.style';

export type AnswerObject ={
  question: string;
  answer: string;
  correct: boolean;
  correctAnswer: string;
}

const TOTAL_QUESTIONS = 10;

const App = () => {

  const[loading, setLoading] = useState(false);
  const[questions, setQuestions] = useState<QuestionState[]>([]);
  const[number, setNumbers] = useState(0);
  const[userAnswer, setUserAnswers] = useState<AnswerObject[]>([]);
  const[score, setScore] = useState(0);
  const[gameOver, setGameOver] =  useState(true);


  const startTrivia = async () =>
  {
    setLoading(true);
    setGameOver(false);

    const newQuestions = await fetchQuizQuestions(TOTAL_QUESTIONS, Difficulty.EASY);

    setQuestions(newQuestions);
    setScore(0);
    setUserAnswers([]);
    setNumbers(0);
    setLoading(false);
  };


  const checkAnswer = (e: any) =>
  {
    if(!gameOver){
      // users answer
      const answer = e.currentTarget.value;
      // check answer against correct answer
      const correct = questions[number].correct_Answer === answer;
      // add score if answer is correct
      if(correct) setScore(prev => prev + 1);
      // save answer in the array for user answers
      const AnswerObject = {
        question: questions[number].question,
        answer,
        correct,
        correctAnswer: questions[number].correct_Answer,
      };
      setUserAnswers((prev) => [...prev, AnswerObject]);
    }

  }
  const nextQuestion = () =>{
    // move on to the next question
    const nextQuestion = number + 1;

    if(nextQuestion === TOTAL_QUESTIONS){
      setGameOver(true);
    }else{setNumbers(nextQuestion);}
  }
  return (
    <>
    <GlobalStyle />
    <Wrapper>
      <h1>REACT QUIZ</h1>
      {gameOver || userAnswer.length === TOTAL_QUESTIONS ?(
        <button className='start' onClick={startTrivia}>
          Start
        </button>
      ) : null}

      {!gameOver ? <p className='score'>Score: {score}</p> : null} 
      {loading ? <p>Loading Questions ...</p> :null }
      {!loading && !gameOver &&(
        <QuestionCard 
          questionNr={number + 1}
          totalQuestions = {TOTAL_QUESTIONS}
          question ={questions[number].question}
          answers = {questions[number].answers}
          userAnswer ={userAnswer ? userAnswer[number]: undefined}
          callback = {checkAnswer}
        />
        )}  
        {!gameOver && !loading && userAnswer.length === number + 1 && number !== TOTAL_QUESTIONS - 1 ?(
           <button className= 'next' onClick={nextQuestion}> Next Question</button> 
        ) :null };

      </Wrapper>
      </>
  );
};

export default App;
